targets.expr:
100
* sum(
    rate(
      container_cpu_usage_seconds_total{
        namespace="$namespace",
        pod=~"$pod",
        container!="$container"
      }[$__rate_interval]
    )
  )
by (pod)

sum by(status_group)(
  count_over_time(
    {namespace="$namespace", pod=~"$pod", node=~"$node"}
    | regexp "(?P<status_group>[2-5]\\d{2})"
    [5m]
  )
)

histogram_quantile(0.95,
  sum by (le) (
    rate(http_request_duration_seconds_bucket{
      namespace="$namespace",
      pod=~"$pod",
      node=~"$node"
    }[5m])
  )
)
